INSERT INTO `baiviet` (`id_baiviet`, `tieude`, `noidung`, `ghichu`) VALUES
(1, 'SỬA MÁY TÍNH', '<p style="text-align: center">&nbsp;</p>\r\n<p><span style="color: rgb(255, 0, 0);"> </span></p>\r\n<p class="MsoNormal" style="text-align: center;"><span style="color: rgb(0, 0, 255);">&nbsp; <span style="font-size: xx-large;"><font face="Times New Roman, serif"><b>Dịch vụ sửa m&aacute;y t&iacute;nh tận nh&agrave;</b></font></span></span><font face="Times New Roman, serif"><span style="font-size: 27px;"><b>&nbsp;</b></span></font></p>\r\n<p class="MsoNormal" style="text-align: center;"><span style="color: rgb(0, 0, 255);"><span style="font-size: xx-large;"><font face="Times New Roman, serif"><b>Dịch vụ sửa m&aacute;y t&iacute;nh tại c&ocirc;ng ty&nbsp;</b></font></span></span><font face="Times New Roman, serif"><span style="font-size: 27px;"><b>&nbsp;</b></span></font></p>\r\n<p style="text-align: center;"><span style="color: rgb(255, 0, 0);"><b><span style="font-size: 18pt; font-family: Arial, sans-serif; border: 1pt none windowtext; padding: 0in; background: rgb(246, 250, 255);">Uy t&iacute;n &ndash; Chất lượng &ndash; Chuy&ecirc;n Nghiệp</span></b></span></p>\r\n<p style="text-align: left;"><span style="font-size: medium;"><span style="font-family: Tahoma;">&bull; kỹ thuật vi&ecirc;n chuy&ecirc;n m&ocirc;n cao về m&aacute;y t&iacute;nh, chuy&ecirc;n nghiệp h&oacute;a về nghiệp vụ chăm s&oacute;c kh&aacute;ch h&agrave;ng..</span></span></p>\r\n<p style="text-align: left;"><span style="font-size: medium;"><span style="font-family: Tahoma;">&bull; hệ thống c&aacute;c cửa h&agrave;ng ở c&aacute;c quận TPHCM </span></span></p>\r\n<p style="text-align: left;"><span style="font-size: medium;"><span style="font-family: Tahoma;">=&gt; m&aacute;y t&iacute;nh bị lỗi hay gặp sự cố, bạn cần giải quyết c&ocirc;ng việc ngay</span></span></p>\r\n<p style="text-align: left;"><span style="font-size: medium;"><span style="font-family: Tahoma;">=&gt; bạn chỉ cần gọi <span style="color: rgb(255, 0, 0);">holine 0976 1122 09</span>&nbsp;đến ch&uacute;ng t&ocirc;i, ch&uacute;ng t&ocirc;i sẽ c&oacute; mặt giải quyết nhanh chống cho bạn.</span></span></p>\r\n<p style="text-align: left;"><span style="font-size: medium;"><span style="font-family: Tahoma;">&bull; Chất lượng dịch vụ chuy&ecirc;n nghiệp, linh phụ kiện ch&iacute;nh h&atilde;ng với gi&aacute; v&ocirc; c&ugrave;ng cạnh tranh.</span></span></p>\r\n<p style="text-align: left;">&nbsp;<span style="font-size: medium;"><span style="font-family: Tahoma;">&bull; Chế độ bảo h&agrave;nh sửa chữa nhanh tận nơi.</span></span></p>\r\n<div style="text-align: center;">&nbsp;</div>\r\n<p class="MsoNormal"><b><span style="font-size: 13.5pt; font-family: ''Times New Roman'', serif; color: blue; border: 1pt none windowtext; padding: 0in;">sửa chữa m&aacute;y vi t&iacute;nh , xử l&yacute; v&agrave; khắc phục tất cả c&aacute;c sự cố m&aacute;y vi t&iacute;nh PC, laptop</span></b><span style="font-size:12.0pt;font-family:&#10;&quot;Cambria&quot;,&quot;serif&quot;;mso-fareast-font-family:&quot;Times New Roman&quot;;mso-bidi-font-family:&#10;&quot;Times New Roman&quot;;color:blue"><b>&nbsp;tận nơi ở tất cả c&aacute;c &nbsp;quận TPHCM.</b></span></p>\r\n<p class="MsoNormal">&nbsp;</p>\r\n<p><span style="color: rgb(0, 0, 255);"> </span></p>\r\n<p class="MsoNormal"><span style="font-size: 12pt; font-family: Cambria, serif;"><b>THAY LINH KIỆN M&Aacute;Y T&Iacute;NH TẬN NƠI CH&Iacute;NH H&Atilde;NG GI&Aacute; TỐT, ĐẢM BẢO UY T&Iacute;N, CHẤT LƯỢNG, NHANH CH&Oacute;NG, CHUY&Ecirc;N NGHIỆP&nbsp;</b></span></p>\r\n<p>&nbsp;</p>\r\n<p class="MsoNormal"><span style="color: rgb(0, 0, 255);"><span style="font-size: larger;"><b><span style="font-family: ''Times New Roman'', serif; border: 1pt none windowtext; padding: 0in;">&quot;Để m&aacute;y t&iacute;nh được nhanh ch&oacute;ng sửa chữa v&agrave; kh&ocirc;ng l&agrave;m gi&aacute;n đoạn c&ocirc;ng việc của qu&yacute; kh&aacute;ch h&atilde;y gọi ch&uacute;ng t&ocirc;i gồm những kỷ thuật trẻ v&agrave; năng động&quot;</span></b></span></span></p>\r\n<p class="MsoNormal"><b><span style="font-size:12.0pt;&#10;font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-fareast-font-family:&quot;Times New Roman&quot;;&#10;color:red;border:none windowtext 1.0pt;mso-border-alt:none windowtext 0in;&#10;padding:0in">ch&uacute;ng t&ocirc;i lu&ocirc;n cung cấp dịch vụ bảo h&agrave;nh, bảo tr&igrave; nhanh ch&oacute;ng v&agrave; chuy&ecirc;n nghiệp.</span></b></p>\r\n<p class="MsoNormal"><span style="color: rgb(0, 0, 255);"><b><span style="font-size: 12pt; font-family: Arial, sans-serif; border: 1pt none windowtext; padding: 0in;">ch&uacute;ng t&ocirc;i cập nhật li&ecirc;n tục v&agrave; &aacute;p dụng c&ocirc;ng nghệ mới kết hợp với những thủ thuật ti&ecirc;n tiến nhất, theo sau đ&oacute; l&agrave; kĩ thuật sửa chữa bảo tr&igrave; ở cấp độ cao chuy&ecirc;n nghiệp.&nbsp;m&aacute;y t&iacute;nh của bạn sẽ lu&ocirc;n hoạt động tốt nhất v&agrave; dữ liệu lu&ocirc;n được bảo vệ ở mức độ cao nhất gi&uacute;p bạn lu&ocirc;n an t&acirc;m với dịch vụ của ch&uacute;ng t&ocirc;i.</span></b></span><span style="color: rgb(0, 0, 255);"><b><span style="font-size: 12pt; font-family: Arial, sans-serif; border: 1pt none windowtext; padding: 0in;"><br />\r\n</span></b></span></p>\r\n<p class="MsoNormal"><span style="color: rgb(255, 0, 0);"><span style="font-size: large;">=&gt; tư vấn lắp đặt ph&ograve;ng internet cấu h&igrave;nh cao chuy&ecirc;n nghiệp ph&ugrave; hợp với vốn hiện c&oacute; của kh&aacute;ch h&agrave;ng.</span></span></p>\r\n<p class="MsoNormal"><span style="color: rgb(0, 255, 0);"><span style="font-size: large;">*Dịch vụ sửa m&aacute;y vi tinh:</span></span></p>\r\n<p class="MsoNormal"><span style="color: rgb(0, 0, 0);"><b><span style="font-size: 13.5pt; font-family: Arial, sans-serif; border: 1pt none windowtext; padding: 0in;">=&gt; <a href="http://suamaytinhvnn.com/dich-vu/sua-may-tinh-tan-nha-3.html">sửa m&aacute;y vi t&iacute;nh tận nh&agrave;</a>, tận nơi, sửa laptop tận nh&agrave; tận nơi chuy&ecirc;n nghiệp uy t&iacute;n</span></b></span></p>\r\n<p class="MsoNormal"><b><span style="color: blue; border: 1pt none windowtext; padding: 0in;"><span style="color: black; text-decoration: none;"><font face="Arial, sans-serif" size="4">=&gt;</font><a href="http://suamayvitinh.net/" style="font-family: ''Times New Roman'', serif; font-size: 12pt;">&nbsp;</a></span></span></b><b><span style="font-size: 13.5pt; font-family: ''Times New Roman'', serif;"><a href="http://suamayvitinh.net/index.php?b=bv&amp;id=2.S%E1%BB%ACA%20M%C3%81Y%20T%C3%8DNH%20LAPTOP"><span style="color:black">setup window xp</span><span style="color:black;text-decoration:none;text-underline:none">&nbsp;</span></a>cho m&aacute;y b&agrave;n (PC)</span><span style="font-size: 13.5pt;"><font face="Arial, sans-serif">, laptop</font></span></b></p>\r\n<p class="MsoNormal"><b><span style="font-size: 13.5pt; font-family: Arial, sans-serif; border: 1pt none windowtext; padding: 0in;">=&gt;setup Windows 7 cho m&aacute;y b&agrave;n(PC), laptop, Macbook (air pro)</span></b></p>\r\n<p class="MsoNormal"><b><span style="font-size: 13.5pt; font-family: Arial, sans-serif; border: 1pt none windowtext; padding: 0in;">=&gt;setup Windows 8 cho m&aacute;y b&agrave;n(PC), laptop,&nbsp;</span></b><span style="color: rgb(0, 0, 0);"><b><span style="font-size: 13.5pt; font-family: Arial, sans-serif; border: 1pt none windowtext; padding: 0in;">Macbook (air pro)</span></b></span></p>\r\n<p class="MsoNormal"><span style="font-size: large;"><strong>=&gt; c&agrave;i đặt tất cả c&aacute;c ứng dụng văn ph&ograve;ng, thiết kế đồ họa</strong></span></p>\r\n<p class="MsoNormal"><b><span style="font-size:13.5pt;font-family:&quot;Arial&quot;,&quot;sans-serif&quot;;&#10;mso-fareast-font-family:&quot;Times New Roman&quot;;color:blue;border:none windowtext 1.0pt;&#10;mso-border-alt:none windowtext 0in;padding:0in">=&gt; khắc phục &nbsp;v&agrave; backup m&aacute;y vi t&iacute;nh laptop bị lỗi mail outlook</span></b></p>\r\n<p class="MsoNormal"><strong><span style="font-size: large;"><span style="color: rgb(0, 0, 0);">=&gt; cứu dữ liệu, phục hồi sao lưu dữ liệu</span></span></strong></p>\r\n<p class="MsoNormal"><b><span style="font-size:13.5pt;font-family:&quot;Arial&quot;,&quot;sans-serif&quot;;mso-fareast-font-family:&#10;&quot;Times New Roman&quot;;color:blue;border:none windowtext 1.0pt;mso-border-alt:none windowtext 0in;&#10;padding:0in">=&gt; sửa m&aacute;y t&iacute;nh setup windows bản quyền (window 7, window 8)</span></b></p>\r\n<p class="MsoNormal"><b><span style="font-size: 13.5pt; font-family: Arial, sans-serif; border: 1pt none windowtext; padding: 0in;">=&gt; Phục hồi Window bản quyền cho m&aacute;y t&iacute;nh PC, laptop&nbsp;</span></b></p>\r\n<p class="MsoNormal"><b><span style="font-size:13.5pt;&#10;font-family:&quot;Arial&quot;,&quot;sans-serif&quot;;mso-fareast-font-family:&quot;Times New Roman&quot;;&#10;color:blue;border:none windowtext 1.0pt;mso-border-alt:none windowtext 0in;&#10;padding:0in">=&gt; c&agrave;i đặt v&agrave; cập nhật &nbsp;chương tr&igrave;nh diệt virus mới nhất bảo vệ m&aacute;y c&ugrave;ng dữ liệu.</span></b></p>\r\n<p class="MsoNormal"><b><span style="font-size:13.5pt;font-family:&quot;Arial&quot;,&quot;sans-serif&quot;;&#10;mso-fareast-font-family:&quot;Times New Roman&quot;;color:blue;border:none windowtext 1.0pt;&#10;mso-border-alt:none windowtext 0in;padding:0in">=&gt;sửa m&aacute;y t&iacute;nh cập nhật driver đầy đủ cho m&aacute;y vi t&iacute;nh, laptop</span></b><b><span style="font-size: 13.5pt; font-family: Arial, sans-serif; border: 1pt none windowtext; padding: 0in;"><br />\r\n</span></b><b><span style="font-size: 12pt; font-family: ''Times New Roman'', serif;"><o:p></o:p></span></b></p>\r\n<p class="MsoNormal"><b><span style="font-size: 13.5pt; font-family: Arial, sans-serif; border: 1pt none windowtext; padding: 0in;">=&gt;xử l&yacute; sự cố hệ thống mạng m&aacute;y t&iacute;nh&nbsp;</span></b></p>\r\n<p class="MsoNormal"><b><span style="font-size:13.5pt;font-family:&#10;&quot;Arial&quot;,&quot;sans-serif&quot;;mso-fareast-font-family:&quot;Times New Roman&quot;;color:blue;&#10;border:none windowtext 1.0pt;mso-border-alt:none windowtext 0in;padding:0in">=&gt;</span></b><b><span style="font-size:13.5pt;font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-fareast-font-family:&#10;&quot;Times New Roman&quot;;color:blue;border:none windowtext 1.0pt;mso-border-alt:none windowtext 0in;&#10;padding:0in"><a href="http://suamayvitinh.net">Sửa m&aacute;y vi t&iacute;nh </a>khắc phục lỗi m&aacute;y vi t&iacute;nh nhanh&nbsp;</span></b><b><span style="font-size: 12pt; font-family: ''Times New Roman'', serif;"><o:p></o:p></span></b></p>\r\n<p class="MsoNormal"><b><span style="font-size: 13.5pt; font-family: Arial, sans-serif; border: 1pt none windowtext; padding: 0in;">=&gt;sửa m&aacute;y t&iacute;nh nhập hợp đồng bảo tr&igrave; m&aacute;y t&iacute;nh </span></b><b><span style="font-size:13.5pt;&#10;font-family:&quot;Arial&quot;,&quot;sans-serif&quot;;mso-fareast-font-family:&quot;Times New Roman&quot;;&#10;color:blue;border:none windowtext 1.0pt;mso-border-alt:none windowtext 0in;&#10;padding:0in">-</span></b></p>\r\n<p class="MsoNormal"><b><span style="font-size:13.5pt;&#10;font-family:&quot;Arial&quot;,&quot;sans-serif&quot;;mso-fareast-font-family:&quot;Times New Roman&quot;;&#10;color:blue;border:none windowtext 1.0pt;mso-border-alt:none windowtext 0in;&#10;padding:0in">=&gt;Di chuyển, sao lưu dữ liệu&nbsp;</span></b><b><span style="font-size: 12pt; font-family: ''Times New Roman'', serif;"><o:p></o:p></span></b></p>\r\n<p class="MsoNormal"><b><span style="font-size: 13.5pt; font-family: Arial, sans-serif; border: 1pt none windowtext; padding: 0in;">=&gt;Xử l&yacute; sự cố phần cứng, phần mềm hay c&aacute;c sự cố kh&aacute;c</span></b></p>\r\n<p class="MsoNormal"><span style="font-size: large;">=&gt; sửa tất cả mainboard m&aacute;y b&agrave;n, laptop</span></p>\r\n<p class="MsoNormal"><span style="font-size: large;">=&gt; sửa m&agrave;n h&igrave;nh LCD, LED</span></p>\r\n<p class="MsoNormal" style="margin-bottom: 0.0001pt; text-align: left; vertical-align: baseline;">&nbsp;</p>\r\n<p>&nbsp;</p>', 'dịch vụ bảo trì máy tính'),
(2, 'SỬA  LAPTOP CHUYÊN NGHIỆP', '<p style="text-align: left;"><span style="color: rgb(255, 0, 0);"><span style="font-size: large;">sửa chữa laptop nhanh sử l&yacute; chuy&ecirc;n nghiệp,m&aacute;y t&iacute;nh laptop hư card mạng,m&aacute;y t&iacute;nh laptop hư main,m&aacute;y laptop hư m&agrave;n h&igrave;nh ,m&aacute;y t&iacute;nh hư cục xạc,m&aacute;y laptop mỗi window,c&agrave;i window cho m&aacute;y vi t&iacute;nh latop ,m&aacute;y laptop hư phần cứng,m&aacute;y laptop hư phần mềm ,lỗi wifi ,khắc phục nhanh nhất chuy&ecirc;n nghiệp nhất TP.HCM.</span></span></p>\r\n<p style="text-align: center;"><img height="446" width="550" alt="sửa latop" src="/upload/noidung_baiviet/Laptop.png" /></p>\r\n<p style="text-align: left;"><span style="font-size: large;">c&oacute; c&aacute;ch n&oacute;i đ&uacute;ng hơn l&agrave; tất cả c&aacute;c thiết bị của m&aacute;y m&oacute;c điện tử đều đựợc bảo h&agrave;nh bảo tr&igrave; h&agrave;ng ng&agrave;y h&agrave;ng th&aacute;ng h&agrave;ng giờ h&agrave;ng năm ch&uacute;ng t&ocirc;i nắm đựơc ti&ecirc;u ch&iacute; đ&oacute; ch&uacute;ng t&ocirc;i chuy&ecirc;n s&acirc;u về m&aacute;y m&oacute;c bảo tr&igrave; chu đ&aacute;o</span></p>\r\n<p style="text-align: left;"><span style="font-size: large;">m&aacute;y vi t&iacute;nh latop một thiết bị hiện đại nhất kh&ocirc;ng thể thiếu cho &nbsp;tất cả c&aacute;c ng&agrave;nh về c&ocirc;ng nghệ th&ocirc;ng tin.</span></p>\r\n<p style="text-align: left;"><span style="font-size: large;">&nbsp;đ&atilde; n&oacute;i về c&ocirc;ng nghệ th&igrave; phải n&oacute;i đến những kỹ sư đựơc đ&agrave;o tạo từ c&aacute;c nứơc ti&ecirc;n tiến v&agrave; chuy&ecirc;n nghiệp cho th&agrave;nh phần ph&aacute;t triển nhanh ch&oacute;ng ph&aacute;n pan nhanh ch&oacute;ng phục vụ chu đ&aacute;,m&aacute;y vi t&iacute;nh latop của bạn thường mắc bệnh quan trọng nhất l&agrave; phần cứng c&aacute;ch khắc phục phần cứng v&agrave; ph&aacute;n bệnh rất đơn giản,nếu ch&uacute;ng t&ocirc;i m&igrave;nh nắm đựơc những ưu điểm của