INSERT INTO `baiviet` (`id_baiviet`, `tieude`, `noidung`, `ghichu`, `urlimage`) VALUES
(30, 'SỬA MÁY TÍNH CHUYÊN NGHIỆP', '<p style=\"text-align: center;\"><a href=\"http://suamaytinhvnn.com/\"><span style=\"color: rgb(255, 0, 0);\"><span style=\"font-size: large;\">SỬA M&Aacute;Y T&Iacute;NH</span></span></a><span style=\"color: rgb(255, 0, 0);\"><span style=\"font-size: large;\"> CHẤT LƯỢNG CHUY&Ecirc;N NGHIỆP</span></span></p>\r\n<p><span style=\"font-size: large;\">K&Iacute;NH CH&Agrave;O QU&Yacute; KH&Aacute;CH H&Agrave;NG:</span></p>\r\n<p><span style=\"font-size: large;\">CH&Uacute;NG T&Ocirc;I CHUY&Ecirc;N GIA VỀ C&Ocirc;NG NGHỆ TH&Ocirc;NG TIN CUNG CẤP DỊCH VỤ GIỎI NHẤT .</span></p>\r\n<p><span style=\"font-size: large;\">GIỎI NGHỀ NGHIỆP Đ&Oacute; L&Agrave; ĐIỀU BẮT BUỘC ĐỂ TRƯỞNG TH&Agrave;NH V&Agrave; PH&Aacute;T TRIỂN TRONG GIỚI C&Ocirc;NG NGHỆ TH&Ocirc;NG TIN THỜI BUỔI ,ĐIỀU BẮT BUỘC DOANH NGHIỆP CH&Uacute;NG T&Ocirc;I CUNG CẤP V&Agrave; MONG MUỐN TH&Agrave;NH C&Ocirc;NG L&Agrave; MANG ĐẾN TH&Agrave;NH C&Ocirc;NG V&Agrave; CẤP ĐỘ CHUY&Ecirc;N NGHIỆP CHO MỌI DOANH NGHIỆP.</span></p>\r\n<p><span style=\"font-size: large;\">Một kỹ thuật vi&ecirc;n sửa chữa m&aacute;y t&iacute;nh l&agrave; một người sửa chữa v&agrave; duy tr&igrave; c&aacute;c m&aacute;y t&iacute;nh . Tr&aacute;ch nhiệm của kỹ thuật vi&ecirc;n c&oacute; thể mở rộng để bao gồm việc x&acirc;y dựng, c&agrave;i đặt v&agrave; cập nhật c&aacute;c &nbsp;phần mềm , Kỹ thuật vi&ecirc;n sửa chữa m&aacute;y t&iacute;nh l&agrave;m việc trong một loạt c&aacute;c c&agrave;i đặt , bao gồm cả khu vực c&ocirc;ng v&agrave; tư nh&acirc;n. V&igrave; sự tồn tại tương đối ngắn gọn về nghề nghiệp, tổ chức cung cấp &nbsp;chương tr&igrave;nh đ&agrave;o tạo sửa chữa m&aacute;y t&iacute;nh thường được thực hiện bởi c&aacute;c kỹ thuật vi&ecirc;n gi&agrave;u kinh nghiệm đ&agrave;o tạo ch&iacute;nh quy trong lĩnh vực n&agrave;y .</span></p>\r\n<p><span style=\"font-size: large;\">Một kỹ thuật vi&ecirc;n sửa chữa c&oacute; thể l&agrave;m việc trong một bộ phận của c&ocirc;ng ty c&ocirc;ng nghệ th&ocirc;ng tin , một trung t&acirc;m dịch vụ trung t&acirc;m , hoặc một m&ocirc;i trường kinh doanh m&aacute;y t&iacute;nh b&aacute;n lẻ . Mặc d&ugrave; c&oacute; sự đa dạng rộng lớn của m&ocirc;i trường l&agrave;m việc , tất cả c&aacute;c kỹ thuật vi&ecirc;n m&aacute;y t&iacute;nh thực hiện c&aacute;c qu&aacute; tr&igrave;nh, bao gồm hỗ trợ kỹ thuật . Kỹ thuật vi&ecirc;n c&oacute; kinh nghiệm c&oacute; thể chuy&ecirc;n về c&aacute;c lĩnh vực như phục hồi dữ liệu , quản trị hệ thống , hoặc c&aacute;c hệ thống th&ocirc;ng tin . Một số kỹ thuật l&agrave; tự l&agrave;m chủ hoặc sở hữu một c&ocirc;ng ty cung cấp dịch vụ tự do hoặc chuy&ecirc;n gia tư vấn . Đ&acirc;y l&agrave; loại dao động kỹ thuật từ người c&oacute; sở th&iacute;ch v&agrave; đam m&ecirc; tự nguyện l&agrave;m &nbsp;việc chuy&ecirc;n nghiệp trong lĩnh vực n&agrave;y .</span></p>\r\n<p><span style=\"font-size: large;\">Trục trặc m&aacute;y t&iacute;nh c&oacute; thể l&agrave; từ một thiết lập nhỏ đ&oacute; l&agrave; kh&ocirc;ng ch&iacute;nh x&aacute;c , phần mềm gi&aacute;n điệp , virus, v&agrave; như xa như thay thế phần cứng hoặc một hệ điều h&agrave;nh to&agrave;n bộ . Một số kỹ thuật cung cấp dịch vụ tại chỗ , nơi m&agrave; kh&aacute;ch h&agrave;ng c&oacute; thể xuống xe tại cửa h&agrave;ng sửa chữa . Một số dịch vụ cho thuận tiện.&nbsp;</span></p>\r\n<p><span style=\"font-size: large;\">1 sửa chữa phần cứng</span></p>\r\n<p><span style=\"font-size: large;\">2 sửa chữa phần mềm</span></p>\r\n<p><span style=\"font-size: large;\">Khi c&oacute; thể, kỹ thuật vi&ecirc;n sửa chữa bảo vệ dữ liệu v&agrave; c&aacute;c thiết lập người d&ugrave;ng m&aacute;y t&iacute;nh , v&igrave; vậy m&agrave; sau khi sửa chữa , người sử dụng sẽ kh&ocirc;ng mất bất kỳ dữ liệu v&agrave; ho&agrave;n to&agrave;n c&oacute; thể sử dụng thiết bị với &iacute;t bị gi&aacute;n đoạn, c&aacute;c vấn đề . Giải quyết vấn đề n&agrave;y, c&aacute;c kỹ thuật vi&ecirc;n c&oacute; thể c&oacute; h&agrave;nh động như nhỏ như điều chỉnh một hoặc một số thiết lập hoặc sở th&iacute;ch , nhưng cũng c&oacute; thể &aacute;p dụng kỹ thuật tham gia nhiều hơn như c&agrave;i đặt , gỡ bỏ c&agrave;i đặt , hoặc c&agrave;i đặt lại c&aacute;c g&oacute;i phần mềm kh&aacute;c nhau . Một thủ tục đ&aacute;ng tin cậy , nhưng hơi phức tạp hơn , để giải quyết c&aacute;c vấn đề phần mềm được biết đến như một kh&ocirc;i phục , trong đ&oacute; h&igrave;nh ảnh c&agrave;i đặt ban đầu của m&aacute;y t&iacute;nh (bao gồm cả hệ điều h&agrave;nh v&agrave; c&aacute;c ứng dụng ban đầu ) được b&ocirc;i lại v&agrave;o một ổ đĩa cứng c&oacute; định dạng . N&oacute; cần được biết mặc d&ugrave;, rằng bất cứ điều g&igrave; độc đ&aacute;o.</span></p>', 'SỬA MÁY TÍNH CHUYÊN NGHIỆP', ''),
(33, 'THANH LÝ MÁY VĂN PHÒNG', '<div style=\"font-family: &quot;Times New Roman&quot;, Times, serif; font-size: 14px; margin-bottom: 0pt; text-align: center;\"><span style=\"margin: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: xx-large; line-height: inherit; font-family: &quot;Times New Roman&quot;; vertical-align: baseline;\"><span style=\"margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline;\"><span style=\"margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: arial, helvetica, sans-serif; vertical-align: baseline;\"><span style=\"margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; color: rgb(255, 0, 0);\">Chuy&ecirc;n thanh l&yacute; c&aacute;c loại thiết bị m&aacute;y văn ph&ograve;ng, M&aacute;y t&iacute;nh, Laptop, M&aacute;y in, M&aacute;y photocopy</span></span></span></span></div>\r\n<div style=\"font-family: &quot;Times New Roman&quot;, Times, serif; font-size: 14px; margin-bottom: 0pt; text-align: center;\">&nbsp;</div>\r\n<div style=\"font-family: &quot;Times New Roman&quot;, Times, serif; font-size: 14px; margin-bottom: 0pt; text-align: center;\">&nbsp;<span style=\"color: rgb(255, 0, 0); font-family: &quot;Times New Roman&quot;; font-size: xx-large; text-align: center;\">h&atilde;y gọi đến ch&uacute;ng t&ocirc;i nếu bạn muốn thanh l&yacute; những thiết bị m&aacute;y văn ph&ograve;ng tồn kho đ&atilde; lỗi thời kh&ocirc;ng c&ograve;n đ&aacute;p ứng c&ocirc;ng việc vận h&agrave;nh của c&ocirc;ng ty</span></div>\r\n<div style=\"font-family: &quot;Times New Roman&quot;, Times, serif; font-size: 14px; margin-bottom: 0pt; text-align: center;\">&nbsp;</div>\r\n<div style=\"font-family: &quot;Times New Roman&quot;, Times, serif; font-size: 14px; margin-bottom: 0pt; text-align: center;\"><span style=\"color: rgb(204, 0, 0);\"><strong><span style=\"font-family: &quot;Times New Roman&quot;;\"><span style=\"font-size: xx-large;\">hotline 0976112209 ( b&aacute;o gi&aacute; thanh l&yacute; qua zalo)</span></span></strong></span></div>\r\n<div style=\"font-family: &quot;Times New Roman&quot;, Times, serif; font-size: 14px; margin-bottom: 0pt; text-align: center;\">\r\n<p style=\"font-family: &quot;Times New Roman&quot;;\"><span style=\"margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline;\"><span style=\"margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; color: rgb(0, 0, 255);\"><span style=\"margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: x-large; line-height: inherit; font-family: inherit; vertical-align: baseline;\"><span style=\"font-family: inherit; font-style: inherit; font-variant-ligatures: inherit; font-variant-caps: inherit; font-weight: inherit;\">địa chỉ cty: 118/18 &Acirc;u Dương L&acirc;n, Phường 3, Quận 8</span></span></span></span></p>\r\n</div>\r\n<div style=\"font-family: &quot;Times New Roman&quot;, Times, serif; font-size: 14px; margin-bottom: 0pt; text-align: center;\">\r\n<p class=\"MsoNormal\" style=\"margin-bottom:0in;margin-bottom:.0001pt;line-height:&#10;normal\">&nbsp;<b><span style=\"font-size: 16pt; font-family: &quot;Times New Roman&quot;, serif; color: rgb(51, 51, 51);\">&nbsp;</span><span style=\"font-size: xx-large;\"><span style=\"color: rgb(255, 0, 0);\"><span style=\"font-family: &quot;Times New Roman&quot;, serif;\">=&gt; Cty ch&uacute;ng t&ocirc;i sẽ cho nh&acirc;n vi&ecirc;n đến tận nơi tiếp nhận</span></span></span></b><b><span style=\"font-size: 16pt; font-family: &quot;Times New Roman&quot;, serif;\">&nbsp;</span></b></p>\r\n<p class=\"MsoNormal\" style=\"margin-bottom: 0.0001pt; line-height: normal; text-align: center;\"><b><span style=\"font-size:16.0pt;font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-fareast-font-family:&#10;&quot;Times New Roman&quot;;color:#333333\"><img src=\"/upload/noidung_baiviet/thanhlymayvanphong.png\" alt=\"\" width=\"600\" height=\"560\" /></span></b></p>\r\n<p class=\"MsoNormal\" style=\"margin-bottom:0in;margin-bottom:.0001pt;line-height:&#10;normal\">&nbsp;</p>\r\n<p class=\"MsoNormal\" style=\"margin-bottom:0in;margin-bottom:.0001pt;line-height:&#10;normal\">&nbsp;</p>\r\n<div style=\"text-align: start; margin-bottom: 0pt;\">&nbsp;<span style=\"color: rgb(0, 0, 255);\"><strong><span style=\"font-size: 18pt; line-height: 31.2px;\">Thu mua thanh l&yacute; m&aacute;y văn ph&ograve;ng&nbsp;</span></strong></span>&nbsp;</div>\r\n<div style=\"text-align: start;\">\r\n<p class=\"MsoNormal\" style=\"margin-bottom: 0.0001pt; line-height: normal;\"><b><span style=\"font-size: 16pt; font-family: &quot;Times New Roman&quot;, serif; color: rgb(51, 51, 51);\">- Chuy&ecirc;n mua thanh l&yacute; m&aacute;y t&iacute;nh cũ văn ph&ograve;ng</span></b><b><span style=\"font-size: 16pt; font-family: &quot;Times New Roman&quot;, serif;\">&nbsp;<o:p></o:p></span></b></p>\r\n<p class=\"MsoNormal\" style=\"margin-bottom: 0.0001pt; line-height: normal;\"><b><span style=\"font-size: 16pt; font-family: &quot;Times New Roman&quot;, serif; color: rgb(51, 51, 51);\">- thanh l&yacute; m&aacute;y in cũ,&nbsp;m&aacute;y fax cũ&nbsp;</span></b><b><span style=\"font-size: 16pt; font-family: &quot;Times New Roman&quot;, serif;\">&nbsp;<o:p></o:p></span></b></p>\r\n<p class=\"MsoNormal\" style=\"margin-bottom: 0.0001pt; line-height: normal;\"><b><span style=\"font-size: 16pt; font-family: &quot;Times New Roman&quot;, serif; color: rgb(51, 51, 51);\">- thanh l&yacute; m&aacute;y photocopy cũ</span></b><b><span style=\"font-size: 16pt; font-family: &quot;Times New Roman&quot;, serif;\">&nbsp;<o:p></o:p></span></b></p>\r\n<p class=\"MsoNormal\" style=\"margin-bottom: 0.0001pt; line-height: normal;\"><b><span style=\"font-size: 16pt; font-family: &quot;Times New Roman&quot;, serif; color: rgb(51, 51, 51);\">- thu mua, b&aacute;n thiết bị Văn Ph&ograve;ng cũ, mới, h&agrave;ng&nbsp;tồn kho</span></b><b><span style=\"font-size: 16pt; font-family: &quot;Times New Roman&quot;, serif;\">&nbsp;<o:p></o:p></span></b></p>\r\n<p class=\"MsoNormal\" style=\"margin-bottom: 0.0001pt; line-height: normal;\"><b><span style=\"font-size: 16pt; font-family: &quot;Times New Roman&quot;, serif; color: rgb(51, 51, 51);\">thanh l&yacute; m&aacute;y t&iacute;nh để b&agrave;n, laptop, m&aacute;y in, photo, m&aacute;y scan, fax, thiết bị m&aacute;y t&iacute;nh.</span></b><b><span style=\"font-size: 16pt; font-family: &quot;Times New Roman&quot;, serif;\">&nbsp;<o:p></o:p></span></b></p>\r\n<p class=\"MsoNormal\" style=\"margin-bottom: 0.0001pt; line-height: normal;\"><b><span style=\"font-size: 16pt; font-family: &quot;Times New Roman&quot;, serif; color: rgb(51, 51, 51);\">thanh l&yacute; tất cả thiết bị c&ocirc;ng nghệ lỗi thời kh&ocirc;ng đ&aacute;p ứng được như cầu c&ocirc;ng việc của cty<o:p></o:p></span></b></p>\r\n<p class=\"MsoNormal\" style=\"margin-bottom: 0.0001pt; line-height: normal;\"><b><span style=\"font-size: 16pt; font-family: &quot;Times New Roman&quot;, serif; color: rgb(51, 51, 51);\"><br />\r\n=&gt;thu mua c&aacute;c loại m&aacute;y vi t&iacute;nh qua sử dụng của c&aacute;c&nbsp;Gia đ&igrave;nh cũ, hư : Monitor, cpu,</span></b><b><span style=\"font-size: 16pt; font-family: &quot;Times New Roman&quot;, serif;\">&nbsp;</span></b><b><span style=\"font-size: 16pt; font-family: &quot;Times New Roman&quot;, serif; color: rgb(51, 51, 51);\">mainboard,&nbsp;Case, ổ nguồn, ổ cứng hdd, cdr, ups lưu điện ổn &aacute;p m&aacute;y&nbsp;lạnh...&nbsp;</span></b></p>\r\n</div>\r\n<p class=\"MsoNormal\" style=\"margin-bottom: 0.0001pt; line-height: normal; text-align: left;\"><b><span style=\"font-size:16.0pt;&#10;font